          function waitForConnectionForActivateCall(res) {
            if (res === 'authorized') {
              window.tutorProject.off('connectStatus', waitForConnectionForActivateCall);

              _activateAlanButton(resolve);
            }
          }

          if (alanAudio) {
            switch (state) {
              case DEFAULT:
                try {
                  _activateAlanButton(resolve);
                } catch (e) {
                  currentErrMsg = NO_VOICE_SUPPORT_IN_BROWSER_MSG;
