        }

        if (data && data.web && data.web.hidden === true) {
          hideAlanBtn();
        } else {
          showAlanBtn();
        }
      }

      function onConnectStatusChange(res) {
        console.info('BTN: connectStatus', res);

        if (options.onConnectionStatus) {
          options.onConnectionStatus(res);
        }

        if (res === 'disconnected') {
          switchState(getDefaultBtnState(DISCONNECTED));
        } else if (res === 'authorized') {
          if (previousState) {
            switchState(previousState);
          } else {
            switchState(getDefaultBtnState());
          }
        }
      }

      function onMicStart() {
        // console.log('BTN: mic. started', new Date());
        switchState(LISTENING);
        isAlanActive = true;

        if (window.tutorProject) {
          window.tutorProject.on('text', onTextCbInMicBtn);
          window.tutorProject.on('parsed', onParsedCbInMicBtn);
          window.tutorProject.on('recognized', onRecognizedCbInMicBtn);
          window.tutorProject.on('connectStatus', onConnectStatusChange);
          window.tutorProject.on('options', onOptionsReceived);
        }
      }

      function onMicStop() {
        // console.log('BTN: mic. stopped');
        playSoundOff();
        isAlanSpeaking = false;
        alanAudio.off('micStart', onMicStart);
        alanAudio.off('micStop', onMicStop);
        alanAudio.off('micFail', onMicFail);
        alanAudio.off('playStart', onPlayStart);
        alanAudio.off('playStop', onPlayStop);
        alanAudio.off('command', onCommandCbInMicBtn);
        hideRecognisedText();
        switchState(DEFAULT);
        isAlanActive = false;

        if (window.tutorProject) {
          window.tutorProject.off('text', onTextCbInMicBtn);
          window.tutorProject.off('parsed', onParsedCbInMicBtn);
          window.tutorProject.off('recognized', onRecognizedCbInMicBtn);
          window.tutorProject.off('connectStatus', onConnectStatusChange);
          window.tutorProject.off('options', onOptionsReceived);
        }

        if (options.onMicStopped) {
          options.onMicStopped();
        }
      }

      function onMicFail() {
        // console.log('BTN: mic. failed');
        onMicStop();
        alert(MIC_BLOCKED_MSG);
        switchState(PERMISSION_DENIED);
      }

      function onPlayStart(e) {
        // console.log('BTN: play start');
        isAlanSpeaking = true;
        switchState(SPEAKING);
        turnOffVoiceFn();
      }

      function onPlayStop(e) {
        // console.log('BTN: play stop');
        isAlanSpeaking = false;
        playSoundNext();
        switchState(LISTENING);
        turnOffVoiceFn();
      }

      function onTextCbInMicBtn(e) {
        // console.info('BTN: onTextCb', e, new Date());
        turnOffVoiceFn();
      }

      function onParsedCbInMicBtn(e) {
        // console.info('BTN: onParsedCb', e, new Date());
        turnOffVoiceFn();
        showRecognisedText(e);
      }

      function onRecognizedCbInMicBtn(e) {
        // console.info('BTN: onRecognizedTextCb', e, new Date());
        if (options.onReceiveRecognisedText) {
          options.onReceiveRecognisedText(e);
        }

        if (e.final === true) {
          switchState(UNDERSTOOD);
        } else {
          switchState(INTERMEDIATE);
        }

        showRecognisedText(e);
        turnOffVoiceFn();
      }

      function onCommandCbInMicBtn(e) {
        // console.info('BTN: onCommandCbInMicBtn', e, new Date());
        if (options.onCommand) {
          options.onCommand(e.data);
        }

        switchState(LISTENING);
        turnOffVoiceFn();
      }

      function playSoundOn() {
        if (!soundOnAudioDoesNotExist) {
          soundOnAudio.currentTime = 0;
          soundOnAudio.play().catch(function () {
            console.log("No activation sound, because the user didn't interact with the button");
          });
        }
      }

      function playSoundOff() {
        if (!soundOffAudioDoesNotExist) {
          soundOffAudio.currentTime = 0;
          soundOffAudio.play().catch(function () {
            console.log("No deactivation sound, because the user didn't interact with the button");
          });
        }
      }

      function playSoundNext() {
        if (!soundNextAudioDoesNotExist) {
          soundNextAudio.currentTime = 0;
          soundNextAudio.play().catch(function () {
            console.log("No deactivation sound, because the user didn't interact with the button");
          });
        }
      }

      window.switchState = switchState;

      function switchState(newState) {
        // console.info('BTN: state', newState);
        var tempLogoParts = [],
            i = 0;

        if (newState !== DISCONNECTED) {
          previousState = newState;
        }

        currentErrMsg = null;

        if (newState === DEFAULT) {
          btn.style.animation = '';
          micIcon.style.animation = '';
          micTriangleIcon.style.animation = '';
          btnBgDefault.classList.remove('super-hidden');
          btnBgDefault.style.opacity = onOpacity;
          btnOval1.style.animation = '';
          btnOval2.style.animation = '';
          btnOval1.style.opacity = 0;
          btnOval2.style.opacity = 0;
          changeBgColors(DEFAULT);
          micIcon.style.opacity = 1;
          micTriangleIcon.style.opacity = 0;
          hideLayers([btnBgListening, btnBgSpeaking, btnBgIntermediate, btnBgUnderstood]);
        } else if (newState === LISTENING) {
          btn.style.animation = pulsatingAnimation;
          micIcon.style.animation = pulsatingMicAnimation;
          micTriangleIcon.style.animation = pulsatingTriangleMicAnimation;
          btnBgListening.classList.remove('super-hidden');
          btnBgListening.style.opacity = onOpacity;
          btnOval1.style.opacity = 1;
          btnOval2.style.opacity = 1;
          changeBgColors(LISTENING);
          micIcon.style.opacity = 1;
          micTriangleIcon.style.opacity = 1;
          hideLayers([btnBgSpeaking, btnBgIntermediate, btnBgUnderstood]);
        } else if (newState === SPEAKING) {
          hideRecognisedText();
          btn.style.animation = pulsatingAnimation;
          btnBgSpeaking.classList.remove('super-hidden');
          btnBgSpeaking.style.opacity = onOpacity;
          btnOval1.style.opacity = 1;
          btnOval2.style.opacity = 1;
          changeBgColors(SPEAKING);
          hideLayers([btnBgDefault, btnBgListening, btnBgIntermediate, btnBgUnderstood]);
        } else if (newState === INTERMEDIATE) {
          btn.style.animation = pulsatingAnimation;
          btnBgIntermediate.classList.remove('super-hidden');
          btnBgIntermediate.style.opacity = onOpacity;
          btnOval1.style.opacity = 1;
          btnOval2.style.opacity = 1;
          changeBgColors(INTERMEDIATE);
          micIcon.style.opacity = 1;
          micTriangleIcon.style.opacity = 1;
          hideLayers([btnBgDefault, btnBgListening, btnBgSpeaking, btnBgUnderstood]);
        } else if (newState === UNDERSTOOD) {
          btn.style.animation = pulsatingAnimation;
          btnBgUnderstood.classList.remove('super-hidden');
          btnBgUnderstood.style.opacity = onOpacity;
          btnOval1.style.opacity = 1;
          btnOval2.style.opacity = 1;
          changeBgColors(UNDERSTOOD);
          micIcon.style.opacity = 1;
          micTriangleIcon.style.opacity = 1;
          hideLayers([btnBgDefault, btnBgListening, btnBgSpeaking, btnBgIntermediate]);
        }

        if (newState === SPEAKING) {
          micTriangleIcon.style.opacity = 0;
          micCircleIcon.style.opacity = 1;
          micTriangleIcon.style.backgroundSize = '0% 0%';
          micCircleIcon.style.backgroundSize = '100% 100%';
        } else {
          micCircleIcon.style.opacity = 0;
          micCircleIcon.style.backgroundSize = '0% 0%';
          micTriangleIcon.style.backgroundSize = '100% 100%';
        }

        if (newState === DEFAULT) {
          micTriangleIcon.classList.add('triangleMicIconBg-default');
        } else {
          micTriangleIcon.classList.remove('triangleMicIconBg-default');
        }

        tempLogoParts = [logoState1, logoState2, logoState3, logoState4, logoState5, logoState6, logoState7, logoState8, logoState9, logoState10];

        if (!customBtnLogoUrl) {
          if (newState === LISTENING || newState === INTERMEDIATE || newState === SPEAKING || newState === UNDERSTOOD) {
            if (logoState1.style.animationName === '') {
              for (i = 0; i < tempLogoParts.length; i++) {
                if (i === 0) {
                  tempLogoParts[i].style.opacity = 1;
                } else {
                  tempLogoParts[i].style.opacity = 0;
                }

                tempLogoParts[i].style.animationName = 'logo-state-' + (i + 1) + '-animation';
              }
            }

            defaultStateBtnIcon.style.opacity = 0;
          } else {
            defaultStateBtnIcon.style.opacity = 1;

            for (i = 0; i < tempLogoParts.length; i++) {
              tempLogoParts[i].style.opacity = 0;
              tempLogoParts[i].style.animationName = '';
            }
          }
        }

        if (newState === LOW_VOLUME || newState === PERMISSION_DENIED || newState === NO_VOICE_SUPPORT || newState === NOT_SECURE_ORIGIN) {
          if (newState === LOW_VOLUME) {
            rootEl.classList.add("alan-btn-low-volume");
            currentErrMsg = LOW_VOLUME_MSG;
          } else if (newState === PERMISSION_DENIED) {
            rootEl.classList.add("alan-btn-permission-denied");
            currentErrMsg = MIC_BLOCKED_MSG;
          } else if (newState === NO_VOICE_SUPPORT || newState === NOT_SECURE_ORIGIN) {
            rootEl.classList.add("alan-btn-no-voice-support");

            if (newState === NO_VOICE_SUPPORT) {
              currentErrMsg = NO_VOICE_SUPPORT_IN_BROWSER_MSG;
            } else if (newState === NOT_SECURE_ORIGIN) {
              currentErrMsg = NOT_SECURE_ORIGIN_MSG;
            }
          }

          if (newState === NO_VOICE_SUPPORT) {
            noVoiceSupportMicIcon.style.opacity = 1;
            lowVolumeMicIcon.style.opacity = 0;
          } else {
            noVoiceSupportMicIcon.style.opacity = 0;
            lowVolumeMicIcon.style.opacity = 1;
          }

          micIcon.style.opacity = 0;
          micTriangleIcon.style.opacity = 0;
          disconnectedMicLoaderIcon.style.opacity = 0;
          offlineIcon.style.opacity = 0;
          btnOval1.style.animation = '';
          btnOval2.style.animation = '';
          btnOval1.style.opacity = 0;
          btnOval2.style.opacity = 0;
        } else if (newState === DISCONNECTED || newState === OFFLINE) {
          if (newState === DISCONNECTED) {
            rootEl.classList.add("alan-btn-disconnected");
          }

          if (newState === OFFLINE) {
            rootEl.classList.add("alan-btn-offline");
            currentErrMsg = OFFLINE_MSG;
          }

          micTriangleIcon.style.opacity = 0;
          lowVolumeMicIcon.style.opacity = 0;
          btnOval1.style.animation = '';
          btnOval2.style.animation = '';
          btnOval1.style.opacity = 0;
          btnOval2.style.opacity = 0;

          if (newState === DISCONNECTED) {
            micIcon.style.opacity = 0;
            disconnectedMicLoaderIcon.style.opacity = 1;
          } else {
            micIcon.style.opacity = 0;
            disconnectedMicLoaderIcon.style.opacity = 0;
            offlineIcon.style.opacity = 1;
          }
        } else {
          lowVolumeMicIcon.style.opacity = 0;
          offlineIcon.style.opacity = 0;
          disconnectedMicLoaderIcon.style.opacity = 0;
          rootEl.classList.remove("alan-btn-low-volume");
          rootEl.classList.remove("alan-btn-permission-denied");
          rootEl.classList.remove("alan-btn-disconnected");
          rootEl.classList.remove("alan-btn-offline");
          rootEl.classList.remove("alan-btn-no-voice-support");
        }

        state = newState;
      } //#endregion
      //#region Helpers


      function applyBgStyles(el, backgroundImage) {
        el.style.transition = 'all 0.4s linear';
        el.style.position = 'absolute';
        el.style.top = '0px';
        el.style.left = '0px';
        el.style.width = '100%';
        el.style.height = '100%';
        el.style.borderRadius = '50%';
        el.style.zIndex = btnBgLayerZIndex;
        el.style.backgroundPosition = '0 0';
        el.style.opacity = 0;
        el.style.opacity = 0;
        el.style.transition = 'opacity 300ms ease-in-out';
        el.style.animation = gradientAnimation;
      }

      function hideLayers(layers) {
        for (var i = 0; i < layers.length; i++) {
          layers[i].style.opacity = offOpacity;
          layers[i].classList.add('super-hidden');
        }
      }

      function changeBgColors(state) {
        var tempBgLayers = [btnOval1, btnOval2];
        var newStateName = state || DEFAULT;
        var tempBgLayerClasses = ['alanBtn-oval-bg-' + DEFAULT, 'alanBtn-oval-bg-' + LISTENING, 'alanBtn-oval-bg-' + INTERMEDIATE, 'alanBtn-oval-bg-' + UNDERSTOOD, 'alanBtn-oval-bg-' + SPEAKING];

        for (var i = 0; i < tempBgLayers.length; i++) {
          for (var j = 0; j < tempBgLayerClasses.length; j++) {
            tempBgLayers[i].classList.remove(tempBgLayerClasses[j]);
          }

          tempBgLayers[i].classList.add('alanBtn-oval-bg-' + newStateName);
        }
      }

      function getStorageKey() {
        var key = '';

        if (options && options.key) {
          key = options.key;
        }

        return 'alan-btn-options-' + key;
      }

      function isMobile() {
        if (/Android|webOS|iPhone|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) {
          return true;
        }

        return false;
      }

      function isOriginSecure() {
        var isSecure = false;
        var protocol = window.location.protocol;
        var hostname = window.location.hostname;

        if (protocol === 'https:') {
          isSecure = true;
        }

        if (protocol === 'file:') {
          isSecure = true;
        }

        if (protocol === 'http:' && (hostname.indexOf('localhost') > -1 || hostname.indexOf('127.0.0.1') > -1)) {
          isSecure = true;
        }

        return isSecure;
      }

      function isAudioSupported() {
        var available = false,
            fakeGetUserMedia,
            fakeContext;
        fakeGetUserMedia = navigator.getUserMedia || navigator.webkitGetUserMedia || navigator.mozGetUserMedia || navigator.msGetUserMedia;
        fakeContext = window.AudioContext || window.webkitAudioContext || window.mozAudioContext;

        if (fakeGetUserMedia && fakeContext) {
          available = true;
        }

        return available;
      }

      function getDebugInfo() {
        var info = '\nDebug Info:\n';
        info += 'getUserMedia: ';
        info += navigator.getUserMedia ? 'true' : 'false';
        info += ' ';
        info += '(mediaDevices.getUserMedia: ';
        info += navigator.mediaDevices && navigator.mediaDevices.getUserMedia ? 'true' : 'false';
        info += ', ';
        info += 'webkit: ';
        info += navigator.webkitGetUserMedia ? 'true' : 'false';
        info += ', ';
        info += 'moz: ';
        info += navigator.mozGetUserMedia ? 'true' : 'false';
        info += ', ';
        info += 'ms: ';
        info += navigator.msGetUserMedia ? 'true' : 'false';
        info += '), ';
        info += 'AudioContext: ';
        info += window.AudioContext ? 'true' : 'false';
        info += ' ';
        info += '(webkit: ';
        info += window.webkitAudioContext ? 'true' : 'false';
        info += ', ';
        info += 'moz: ';
        info += window.mozAudioContext ? 'true' : 'false';
        info += '), ';
        info += 'userAgent - ';
        info += navigator.userAgent;
        return info;
      } //#endregion
      //#region Append layers to the rootEl


      function showAlanBtn() {
        rootEl.innerHTML = '';
        recognisedTextHolder.appendChild(recognisedTextContent);
        rootEl.appendChild(recognisedTextHolder);
        rootEl.appendChild(btn);
        btnDisabled = false;
      }

      function hideAlanBtn() {
        if (!isTutorMode()) {
          alanAudio.stop();
          rootEl.innerHTML = '';
          btnDisabled = true;
        }
      }

      function showSpeach2TextPanel() {
        hideS2TPanel = false;
      }

      function hideSpeach2TextPanel() {
        hideS2TPanel = true;
        hideRecognisedText();
      }

      function applyBtnOptions(btnOptions) {
        if (btnOptions) {
          createAlanStyleSheet(btnOptions);
        }
      }

      function applyLogoOptions(logoUrl) {
        if (logoUrl) {
          customBtnLogoUrl = logoUrl;
          defaultStateBtnIcon.src = logoUrl;
          micIcon.src = logoUrl;
          micTriangleIcon.src = logoUrl;
          micTriangleIcon.classList.remove('triangleMicIconBg'); // logoState1.src=logoUrl;
          // logoState2.src=logoUrl;
          // logoState3.src=logoUrl;
          // logoState4.src=logoUrl;
          // logoState5.src=logoUrl;
          // logoState6.src=logoUrl;
          // logoState7.src=logoUrl;
          // logoState8.src=logoUrl;
          // logoState9.src=logoUrl;
          // logoState10.src=logoUrl;
        }
      }

      rootEl.classList.add("alanBtn-root");
      rootEl.classList.add("alan-" + getProjectId());
      var alanBtnSavedOptions = null;

      if (isTutorMode()) {
        showAlanBtn();
      } else {
        if (isLocalStorageAvailable) {
          try {
            tryReadSettingsFromLocalStorage();
          } catch (e) {
            showAlanBtn();
          }
        } else {
          showAlanBtn();
        }
      }

      function tryReadSettingsFromLocalStorage() {
        if (isLocalStorageAvailable) {
          try {
            alanBtnSavedOptions = JSON.parse(localStorage.getItem(getStorageKey()));

            if (alanBtnSavedOptions && alanBtnSavedOptions.web) {
              if (alanBtnSavedOptions.web.btnOptions) {
                applyBtnOptions(alanBtnSavedOptions.web.btnOptions);
              }

              if (alanBtnSavedOptions.web.hidden !== true) {
                showAlanBtn();
              } else {
                hideAlanBtn();
              }
            }
          } catch (e) {}
        }
      }

      if (!options.rootEl) {
        body.appendChild(rootEl);
      } //#endregion
      //#region Drag-n-drop btn logic


      var dndInitialMousePositions = [0, 0];
      var dndIsDown = false;
      var afterMouseMove = false;
      var dndSideBtnPos = null;
      var dndTopPos;
      var dndBottomPos;
      var dndFinalLeftPos;
      var dndFinalRightPos;
      var dndAnimDelay = 300;
      var dndAnimTransition = dndAnimDelay + 'ms';
      var dndBackAnimFinished = true;

      if (!pinned) {
        rootEl.addEventListener('mousedown', onMouseDown, true);
        rootEl.addEventListener('touchstart', onMouseDown, {
          passive: false
        });
        document.addEventListener('mouseup', onMouseUp, true);
        document.addEventListener('touchend', onMouseUp, {
          passive: false
        });
        document.addEventListener('mousemove', onMouseMove, true);
        document.addEventListener('touchmove', onMouseMove, {
          passive: false
        });
      }

      function onMouseDown(e) {
        var posInfo = e.touches ? e.touches[0] : e;
        if (!dndBackAnimFinished || e.buttons !== undefined && e.buttons !== 1) return;
        dndIsDown = true;
        rootEl.style.transition = '0ms';

        if (!dndSideBtnPos) {
          dndSideBtnPos = isLeftAligned ? rootEl.offsetLeft : window.innerWidth - rootEl.offsetLeft;
        }

        dndInitialMousePositions = [posInfo.clientX, posInfo.clientY];

        if (isTopAligned) {
          dndTopPos = +rootEl.style.top.replace('px', '');
        } else {
          dndBottomPos = +rootEl.style.bottom.replace('px', '');
        }
      }

      function onMouseUp(e) {
        var curPos;
        var posInfo;

        if (dndIsDown) {
          posInfo = e.touches ? e.touches[0] : e;
          dndIsDown = false;
          rootEl.style.transition = dndAnimTransition;

          if (isLeftAligned) {
            curPos = +rootEl.style.left.replace('px', '');
          } else {
            curPos = +rootEl.style.right.replace('px', '');
          }

          if (curPos <= window.innerWidth / 2) {
            if (posInfo && Math.abs(dndInitialMousePositions[0] - posInfo.clientX) < 15 && Math.abs(dndInitialMousePositions[1] - posInfo.clientY) < 15) {
              afterMouseMove = false;
            }

            moveBtnToTheSameSide();
          } else {
            moveBtnToTheOppositeSide();
          }

          setTimeout(function () {
            afterMouseMove = false;
          }, 300);
        }
      }

      function onMouseMove(e) {
        var posInfo = e.touches ? e.touches[0] : e;

        if (dndIsDown) {
          hideRecognisedText(0, true);
          e.preventDefault();
          afterMouseMove = true;

          if (isLeftAligned) {
            dndFinalLeftPos = dndSideBtnPos + posInfo.clientX - dndInitialMousePositions[0];
            dndFinalRightPos = window.innerWidth - dndFinalLeftPos;
            rootEl.style.left = correctXPos(dndSideBtnPos + posInfo.clientX - dndInitialMousePositions[0]) + 'px';
          } else {
            dndFinalRightPos = dndSideBtnPos - posInfo.clientX + dndInitialMousePositions[0];
            dndFinalLeftPos = window.innerWidth - dndFinalRightPos;
            rootEl.style.right = correctXPos(dndSideBtnPos - posInfo.clientX + dndInitialMousePositions[0]) + 'px';
          }

          if (isTopAligned) {
            rootEl.style.top = correctYPos(dndTopPos + posInfo.clientY - dndInitialMousePositions[1]) + 'px';
          } else {
            rootEl.style.bottom = correctYPos(dndBottomPos - posInfo.clientY + dndInitialMousePositions[1]) + 'px';
          }
        }
      }

      function correctYPos(yPos) {
        var defDelta = 10;

        if (yPos < 0) {
          return defDelta;
        } else if (yPos > window.innerHeight - btnSize - defDelta) {
          return window.innerHeight - btnSize - defDelta;
        }

        return yPos;
      }

      function correctXPos(xPos) {
        var defDelta = 10;

        if (xPos < 0) {
          return defDelta;
        } else if (xPos > window.innerWidth - btnSize - defDelta) {
          return window.innerWidth - btnSize - defDelta;
        }

        return xPos;
      }

      function changeBtnSide(side) {
        if (side === 'to-left') {
          rootEl.style.right = '';
          isLeftAligned = true;
          isRightAligned = false;
        } else {
          rootEl.style.left = '';
          isLeftAligned = false;
          isRightAligned = true;
        }

        setStylesBasedOnSide();
      }

      function moveBtnToTheSameSide() {
        rootEl.style.transition = dndAnimTransition;
        rootEl.style[isLeftAligned ? 'left' : 'right'] = dndSideBtnPos + 'px';
        setTextPanelPosition(recognisedTextHolder);
      }

      function moveBtnToTheOppositeSide() {
        var finalBtnPos = window.innerWidth - dndSideBtnPos - btnSize + 'px';
        dndBackAnimFinished = false;

        if (isLeftAligned) {
          rootEl.style.left = finalBtnPos;
          setTimeout(function () {
            isLeftAligned = false;
            rootEl.style.right = dndSideBtnPos + 'px';
            setTextPanelPosition(recognisedTextHolder);
            changeBtnSide('to-right');
            dndBackAnimFinished = true;
          }, dndAnimDelay);
        } else {
          rootEl.style.right = finalBtnPos;
          setTimeout(function () {
            rootEl.style.left = dndSideBtnPos + 'px';
            isLeftAligned = true;
            setTextPanelPosition(recognisedTextHolder);
            changeBtnSide('to-left');
            dndBackAnimFinished = true;
          }, dndAnimDelay);
        }
      } //#endregion


      return btnInstance;
    }

    ns.alanBtn = alanBtn;
  })(window);

  return alanBtn;
});

