    function generateKeyFrame(name, rule) {
        var prefixes = ['@-webkit-keyframes', '@keyframes'];
        var r = '';
        for (var i = 0; i < prefixes.length; i++) {
            r += prefixes[i] + ' ' + name + '{' + rule + '} ';
        }
        return r;
    }

